import os
import json
from openai import OpenAI
os.environ['http_proxy'] = 'http://127.0.0.1:7890'
os.environ['https_proxy'] = 'http://127.0.0.1:7890'
# 1. 配置你的 API (可以用 OpenAI，也可以用兼容 OpenAI 格式的 DeepSeek/Qwen 等)
client = OpenAI(
    # This is the default and can be omitted
    base_url=os.getenv('OPENAI_API_BASE'),
    api_key=os.getenv('GEMINI_API_KEY')
)
MODEL_NAME = "gemini-2.5-pro"  # 或者 "deepseek-chat"


def  get_plan(transcript_text):
    """
    核心逻辑：读取 Prompt 模板 -> 填充 Transcript -> 调用 LLM 生成大纲
    """
    # 读取我们在第一步创建的模板
    try:
        with open('prompts/plan.txt', encoding='utf-8') as f:
            template = f.read()
    except FileNotFoundError:
        print("错误：请先创建 prompts/plan.txt 文件！")
        return None

    # 替换占位符 $INST$
    prompt = template.replace('$INST$', transcript_text)

    print(f"正在为 {len(transcript_text)} 字的文本生成大纲...")

    try:
        response = client.chat.completions.create(
            model=MODEL_NAME,
            messages=[
                {"role": "user", "content": prompt}
            ],
            temperature=0.7,  # 稍微有点创造性
            max_tokens=8000 , # 大纲通常不会特别长
        timeout = 400
        )
        return response.choices[0].message.content
    except Exception as e:
        print(f"API 调用出错: {e}")
        return None


